import { HttpClient } from '@angular/common/http';
import { Component, Inject } from '@angular/core';
import { UserLoginViewModel } from '../models/user-login.model';
import { ProductViewModel } from '../models/product.model';
import { Observable } from 'rxjs';
import { CartViewModel } from '../models/cart.model';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
  public username: string = 'sam_smith';
  public password: string = 'PASSword!234';
  public signInSuccess?: boolean;
  public products$: Observable<ProductViewModel[]> = new Observable<ProductViewModel[]>();
  public cart: CartViewModel = { cartItems: [] } as CartViewModel;
  public cartTotal = 0;

  constructor(private readonly http: HttpClient, @Inject('BASE_URL') private readonly baseUrl: string) {

  }

  public login() {
    const userLoginModel: UserLoginViewModel = {
      username: this.username,
      password: this.password
    };

    this.http.post<boolean>(this.baseUrl + 'user/login', userLoginModel).subscribe(result => {
      this.signInSuccess = result;

      if (this.signInSuccess)
        this.products$ = this.getProducts();
    }, error => console.error(error));
  }

  public addToCart(product: ProductViewModel) {
    this.cart.cartItems.push({
      productId: product.id,
      name: product.name,
      code: product.code,
      price: product.price,
      volumeDiscountQuantity: product.volumeDiscountQuantity
    });
  }

  public removeFromCart(idx: number) {
    this.cart.cartItems.splice(idx, 1);
  }

  public calculateTotal() {
    this.http.post<number>(this.baseUrl + 'cart/calculateTotal', this.cart).subscribe(result => {
      this.cartTotal = result;
    }, error => console.error(error));
  }

  private getProducts(): Observable<ProductViewModel[]> {
    return this.http.get<ProductViewModel[]>(this.baseUrl + 'product');
  }
}


<div *ngIf="!signInSuccess">
  <h1>Hi shopper, please login to begin shopping!</h1>

  <p>
    Username:
    <input type="text" [(ngModel)]="username" />
  </p>

  <p>
    Username:
    <input type="password" [(ngModel)]="password" />
  </p>

  <p>
    <button class="btn btn-primary" (click)="login()">Login</button>
  </p>

  <p *ngIf="signInSuccess === false">
    Sorry, please try signing in again.
  </p>
</div>


<div *ngIf="signInSuccess">
  <h1>Hi {{username}}, let's shop!</h1>

  <h4>Add items to your cart in any order you like</h4>

  <div style="padding-top: 15px; padding-bottom: 15px;">
    <ng-container *ngIf="products$ | async as products">
      <table class='table table-striped' aria-labelledby="tableLabel" *ngIf="products.length > 0">
        <thead>
          <tr>
            <th>Name</th>
            <th>Code</th>
            <th>Price</th>
            <th>Discount Quantity</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let product of products">
            <td>{{ product.name }}</td>
            <td>{{ product.code }}</td>
            <td style="width: 100px; padding-right: 25px; text-align: right;">{{ product.price | currency:'USD' }}</td>
            <td style="width: 200px; padding-right: 50px; text-align: right;">{{ product.volumeDiscountQuantity }}</td>
            <td style="width: 200px;"><button class="btn btn-primary" (click)="addToCart(product)">Add To Cart</button></td>
          </tr>
        </tbody>
      </table>
    </ng-container>
  </div>

  <h4>Calculate your total</h4>

  <div style="padding-top: 15px; padding-bottom: 15px;">
    <table class='table table-striped' aria-labelledby="tableLabel" *ngIf="cart.cartItems.length > 0">
      <thead>
        <tr>
          <th>Name</th>
          <th>Code</th>
          <th>Price</th>
          <th>Discount Quantity</th>
          <th>Action</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let cartItem of cart.cartItems; let idx = index">
          <td>{{ cartItem.name }}</td>
          <td>{{ cartItem.code }}</td>
          <td style="width: 100px; padding-right: 25px; text-align: right;">{{ cartItem.price | currency:'USD' }}</td>
          <td style="width: 200px; padding-right: 25px; text-align: right;">{{ cartItem.volumeDiscountQuantity }}</td>
          <td style="width: 200px;"><button class="btn btn-primary" (click)="removeFromCart(idx)">Remove From Cart</button></td>
        </tr>
      </tbody>
    </table>
    <button class="btn btn-primary" (click)="calculateTotal()">Calculate Total</button>
  </div>

  <div style="padding-top: 15px; padding-bottom: 15px;">
    Total:
    {{ cartTotal | currency:'USD' }}
  </div>

</div>


