        this.products = result;
      }, error => console.error(error));
      //this.products$ = this.getProducts();
    }, error => console.error(error));
  }
  addToCart(product) {
    this.cart.cartItems.push({
      productId: product.id,
      name: product.name,
      code: product.code,
      price: product.price,
      volumeDiscountQuantity: product.volumeDiscountQuantity,
      volumeDiscountPrice: product.volumeDiscountPrice
    });
    this.cartTotal = 0;
  }
  addToCartByProductCodes(productCodes) {
    this.cart.cartItems = [];
    for (let i = 0; i < productCodes.length; i++) {
      let code = productCodes.charAt(i);
      let product = this.products.find(x => x.code === code);
      this.cart.cartItems.push({
        productId: product.id,
        name: product.name,
        code: product.code,
        price: product.price,
        volumeDiscountQuantity: product.volumeDiscountQuantity,
        volumeDiscountPrice: product.volumeDiscountPrice
      });
    }
    this.cartTotal = 0;
  }
  removeFromCart(idx) {
    this.cart.cartItems.splice(idx, 1);
    this.cartTotal = 0;
  }
  clearCart() {
    this.cart.cartItems = [];
    this.cartTotal = 0;
  }
  calculateTotal() {
    this.http.post(this.baseUrl + 'cart/calculateTotal', this.cart).subscribe(result => {
      this.cartTotal = result;
    }, error => console.error(error));
  }
  getProducts() {
    return this.http.get(this.baseUrl + 'product');
  }
}
